// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeCategory, setActiveCategory] = useState('все');
  const [likes, setLikes] = useState({});

  const photos = [
    {
      id: 1,
      category: 'архитектура',
      src: 'https://i.pinimg.com/736x/6b/c6/10/6bc610ccc015ef7fa26780003109ec88.jpg',
      likes: 1200,
      alt: 'Здание в стиле модерн'
    },
    {
      id: 2,
      category: 'природа',
      src: 'https://i.pinimg.com/1200x/6c/da/54/6cda54bd8a74a541f6643125bc66db6a.jpg',
      likes: 2100,
      alt: 'Лесной пейзаж'
    },
    {
      id: 3,
      category: 'люди',
      src: 'https://i.pinimg.com/736x/36/82/35/36823531ede1c2ef0a52064c69a93332.jpg',
      likes: 1500,
      alt: 'Портрет девушки на улице'
    },
    {
      id: 4,
      category: 'животные',
      src: 'https://i.pinimg.com/736x/17/97/51/179751d9f8a0ced9198999ff110fc5c8.jpg',
      likes: 2800,
      alt: 'Кот на крыше'
    },
    {
      id: 5,
      category: 'архитектура',
      src: 'https://i.pinimg.com/736x/7c/b2/c9/7cb2c90cee1b9dd23e17fa4c8ddea0dc.jpg',
      likes: 1800,
      alt: 'Старинная башня'
    },
    {
      id: 6,
      category: 'природа',
      src: 'https://i.pinimg.com/1200x/3d/a2/2a/3da22a246fcb45d7585b74401fa084c2.jpg',
      likes: 3000,
      alt: 'Река в горах'
    },
    {
      id: 7,
      category: 'люди',
      src: 'https://images.unsplash.com/photo-1508221570519-e05185389706?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      likes: 1300,
      alt: 'Мужчина в городе'
    },
    {
      id: 8,
      category: 'животные',
      src: 'https://images.unsplash.com/photo-1552291977-613465647109?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      likes: 1700,
      alt: 'Собака на прогулке'
    }
  ];

  useEffect(() => {
    const initialLikes = {};
    photos.forEach(photo => {
      initialLikes[photo.id] = photo.likes;
    });
    setLikes(initialLikes);
  }, [photos]); // ← Добавлено!

  const handleLike = (id) => {
    setLikes(prev => ({
      ...prev,
      [id]: prev[id] + 1
    }));
  };

  const filteredPhotos = activeCategory === 'все'
    ? photos
    : photos.filter(photo => photo.category === activeCategory);

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>Анна Иванова</h1>
          <p>Фотография, которая рассказывает историю</p>
        </div>
      </header>

      {/* Categories */}
      <section className="section fade-in-up">
        <div className="container">
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '2rem' }}>
            {['все', 'архитектура', 'природа', 'люди', 'животные'].map((cat) => (
              <button
                key={cat}
                onClick={() => setActiveCategory(cat)}
                className={`category-btn ${activeCategory === cat ? 'active' : ''}`}
              >
                {cat === 'все' ? 'Все' : cat.charAt(0).toUpperCase() + cat.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>{activeCategory === 'все' ? 'Моя работа' : activeCategory === 'архитектура' ? 'Архитектура' : 
               activeCategory === 'природа' ? 'Природа' : 
               activeCategory === 'люди' ? 'Люди' : 'Животные'}</h2>
          <div className="gallery">
            {filteredPhotos.map((photo) => (
              <div key={photo.id} className="gallery-item" style={{ animationDelay: `${photo.id * 0.2}s` }}>
                <img src={photo.src} alt={photo.alt} />
                <div className="like-container" onClick={() => handleLike(photo.id)}>
                  <span className="like-icon">❤️</span>
                  <span className="like-count">{likes[photo.id]}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>Связаться со мной</h2>
          <form
            action="https://formspree.io/f/mvgbvnvr"
            method="POST"
            className="contact-form"
          >
            <input type="hidden" name="_subject" value="📩 Новое сообщение с портфолио фотографа" />

            <div className="form-group">
              <label htmlFor="name">Имя</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Ваше имя"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Ваш email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="message">Сообщение</label>
              <textarea
                id="message"
                name="message"
                placeholder="Чем могу помочь?"
                required
              ></textarea>
            </div>

            <button type="submit">Отправить</button>
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer style={{ textAlign: 'center', padding: '2rem 0', backgroundColor: '#f2f8f2', borderTop: '1px solid #e0e0e0' }}>
        <div className="container">
          <p>&copy; 2025 Анна Иванова. Все права защищены.</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
