// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeCategory, setActiveCategory] = useState('–≤—Å–µ');
  const [likes, setLikes] = useState({});

  const photos = [
    {
      id: 1,
      category: '–∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞',
      src: 'https://i.pinimg.com/736x/6b/c6/10/6bc610ccc015ef7fa26780003109ec88.jpg',
      likes: 1200,
      alt: '–ó–¥–∞–Ω–∏–µ –≤ —Å—Ç–∏–ª–µ –º–æ–¥–µ—Ä–Ω'
    },
    {
      id: 2,
      category: '–ø—Ä–∏—Ä–æ–¥–∞',
      src: 'https://i.pinimg.com/1200x/6c/da/54/6cda54bd8a74a541f6643125bc66db6a.jpg',
      likes: 2100,
      alt: '–õ–µ—Å–Ω–æ–π –ø–µ–π–∑–∞–∂'
    },
    {
      id: 3,
      category: '–ª—é–¥–∏',
      src: 'https://i.pinimg.com/736x/36/82/35/36823531ede1c2ef0a52064c69a93332.jpg',
      likes: 1500,
      alt: '–ü–æ—Ä—Ç—Ä–µ—Ç –¥–µ–≤—É—à–∫–∏ –Ω–∞ —É–ª–∏—Ü–µ'
    },
    {
      id: 4,
      category: '–∂–∏–≤–æ—Ç–Ω—ã–µ',
      src: 'https://i.pinimg.com/736x/17/97/51/179751d9f8a0ced9198999ff110fc5c8.jpg',
      likes: 2800,
      alt: '–ö–æ—Ç –Ω–∞ –∫—Ä—ã—à–µ'
    },
    {
      id: 5,
      category: '–∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞',
      src: 'https://i.pinimg.com/736x/7c/b2/c9/7cb2c90cee1b9dd23e17fa4c8ddea0dc.jpg',
      likes: 1800,
      alt: '–°—Ç–∞—Ä–∏–Ω–Ω–∞—è –±–∞—à–Ω—è'
    },
    {
      id: 6,
      category: '–ø—Ä–∏—Ä–æ–¥–∞',
      src: 'https://i.pinimg.com/1200x/3d/a2/2a/3da22a246fcb45d7585b74401fa084c2.jpg',
      likes: 3000,
      alt: '–†–µ–∫–∞ –≤ –≥–æ—Ä–∞—Ö'
    },
    {
      id: 7,
      category: '–ª—é–¥–∏',
      src: 'https://images.unsplash.com/photo-1508221570519-e05185389706?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      likes: 1300,
      alt: '–ú—É–∂—á–∏–Ω–∞ –≤ –≥–æ—Ä–æ–¥–µ'
    },
    {
      id: 8,
      category: '–∂–∏–≤–æ—Ç–Ω—ã–µ',
      src: 'https://images.unsplash.com/photo-1552291977-613465647109?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      likes: 1700,
      alt: '–°–æ–±–∞–∫–∞ –Ω–∞ –ø—Ä–æ–≥—É–ª–∫–µ'
    }
  ];

  useEffect(() => {
    const initialLikes = {};
    photos.forEach(photo => {
      initialLikes[photo.id] = photo.likes;
    });
    setLikes(initialLikes);
  }, [photos]); // ‚Üê –î–æ–±–∞–≤–ª–µ–Ω–æ!

  const handleLike = (id) => {
    setLikes(prev => ({
      ...prev,
      [id]: prev[id] + 1
    }));
  };

  const filteredPhotos = activeCategory === '–≤—Å–µ'
    ? photos
    : photos.filter(photo => photo.category === activeCategory);

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>–ê–Ω–Ω–∞ –ò–≤–∞–Ω–æ–≤–∞</h1>
          <p>–§–æ—Ç–æ–≥—Ä–∞—Ñ–∏—è, –∫–æ—Ç–æ—Ä–∞—è —Ä–∞—Å—Å–∫–∞–∑—ã–≤–∞–µ—Ç –∏—Å—Ç–æ—Ä–∏—é</p>
        </div>
      </header>

      {/* Categories */}
      <section className="section fade-in-up">
        <div className="container">
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '2rem' }}>
            {['–≤—Å–µ', '–∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞', '–ø—Ä–∏—Ä–æ–¥–∞', '–ª—é–¥–∏', '–∂–∏–≤–æ—Ç–Ω—ã–µ'].map((cat) => (
              <button
                key={cat}
                onClick={() => setActiveCategory(cat)}
                className={`category-btn ${activeCategory === cat ? 'active' : ''}`}
              >
                {cat === '–≤—Å–µ' ? '–í—Å–µ' : cat.charAt(0).toUpperCase() + cat.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>{activeCategory === '–≤—Å–µ' ? '–ú–æ—è —Ä–∞–±–æ—Ç–∞' : activeCategory === '–∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞' ? '–ê—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞' : 
               activeCategory === '–ø—Ä–∏—Ä–æ–¥–∞' ? '–ü—Ä–∏—Ä–æ–¥–∞' : 
               activeCategory === '–ª—é–¥–∏' ? '–õ—é–¥–∏' : '–ñ–∏–≤–æ—Ç–Ω—ã–µ'}</h2>
          <div className="gallery">
            {filteredPhotos.map((photo) => (
              <div key={photo.id} className="gallery-item" style={{ animationDelay: `${photo.id * 0.2}s` }}>
                <img src={photo.src} alt={photo.alt} />
                <div className="like-container" onClick={() => handleLike(photo.id)}>
                  <span className="like-icon">‚ù§Ô∏è</span>
                  <span className="like-count">{likes[photo.id]}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>–°–≤—è–∑–∞—Ç—å—Å—è —Å–æ –º–Ω–æ–π</h2>
          <form
            action="https://formspree.io/f/mvgbvnvr"
            method="POST"
            className="contact-form"
          >
            <input type="hidden" name="_subject" value="üì© –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ —Å –ø–æ—Ä—Ç—Ñ–æ–ª–∏–æ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∞" />

            <div className="form-group">
              <label htmlFor="name">–ò–º—è</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="–í–∞—à–µ –∏–º—è"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="–í–∞—à email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="message">–°–æ–æ–±—â–µ–Ω–∏–µ</label>
              <textarea
                id="message"
                name="message"
                placeholder="–ß–µ–º –º–æ–≥—É –ø–æ–º–æ—á—å?"
                required
              ></textarea>
            </div>

            <button type="submit">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
          </form>
