// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });
  const [errors, setErrors] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = '–ò–º—è –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ';
    if (!formData.email.trim()) {
      newErrors.email = 'Email –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = '–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç email';
    }
    if (!formData.message.trim()) newErrors.message = '–°–æ–æ–±—â–µ–Ω–∏–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ';
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    fetch('https://formspree.io/f/mvgbvnvr', {
      method: 'POST',
      body: JSON.stringify(formData),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (response.ok) {
          setIsSubmitted(true);
          setFormData({ name: '', email: '', message: '' });
          setTimeout(() => setIsSubmitted(false), 3000);
        } else {
          alert('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.');
        }
      })
      .catch((error) => {
        console.error('–û—à–∏–±–∫–∞:', error);
        alert('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ —Å–µ—Ç–∏. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ.');
      });
  };

  const images = [
    'https://i.pinimg.com/736x/6b/c6/10/6bc610ccc015ef7fa26780003109ec88.jpg',
    'https://i.pinimg.com/1200x/6c/da/54/6cda54bd8a74a541f6643125bc66db6a.jpg',
    'https://i.pinimg.com/736x/36/82/35/36823531ede1c2ef0a52064c69a93332.jpg',
    'https://i.pinimg.com/736x/17/97/51/179751d9f8a0ced9198999ff110fc5c8.jpg',
    'https://i.pinimg.com/736x/7c/b2/c9/7cb2c90cee1b9dd23e17fa4c8ddea0dc.jpg',
    'https://i.pinimg.com/1200x/3d/a2/2a/3da22a246fcb45d7585b74401fa084c2.jpg',
  ];

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>–ê–Ω–Ω–∞ –ò–≤–∞–Ω–æ–≤–∞</h1>
          <p>–§–æ—Ç–æ–≥—Ä–∞—Ñ–∏—è, –∫–æ—Ç–æ—Ä–∞—è —Ä–∞—Å—Å–∫–∞–∑—ã–≤–∞–µ—Ç –∏—Å—Ç–æ—Ä–∏—é</p>
        </div>
      </header>

      {/* About */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>–û —Å–µ–±–µ</h2>
          <p>
            –Ø ‚Äî —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ —Å –±–æ–ª–µ–µ —á–µ–º 5-–ª–µ—Ç–Ω–∏–º –æ–ø—ã—Ç–æ–º –≤ –ø–æ—Ä—Ç—Ä–µ—Ç–Ω–æ–π –∏ —É–ª–∏—á–Ω–æ–π —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏.
            –õ—é–±–ª—é –∑–∞–ø–µ—á–∞—Ç–ª–µ–≤–∞—Ç—å –º–æ–º–µ–Ω—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –∫–∞–∂—É—Ç—Å—è –Ω–µ–∑–∞–º–µ—Ç–Ω—ã–º–∏, –Ω–æ –ø–æ–ª–Ω—ã —ç–º–æ—Ü–∏–π.
            –†–∞–±–æ—Ç–∞—é –≤ –º—è–≥–∫–∏—Ö —Å–≤–µ—Ç–∞—Ö, —Å –∞–∫—Ü–µ–Ω—Ç–æ–º –Ω–∞ –µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω–æ—Å—Ç—å –∏ –¥–µ—Ç–∞–ª–∏.
          </p>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>–ú–æ—è —Ä–∞–±–æ—Ç–∞</h2>
          <div className="gallery">
            {images.map((img, index) => (
              <div key={index} className="gallery-item" style={{ animationDelay: `${index * 0.2}s` }}>
                <img src={img.trim()} alt={`–§–æ—Ç–æ ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>–°–≤—è–∑–∞—Ç—å—Å—è —Å–æ –º–Ω–æ–π</h2>
          <form
