// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });
  const [errors, setErrors] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Имя обязательно';
    if (!formData.email.trim()) {
      newErrors.email = 'Email обязателен';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Неверный формат email';
    }
    if (!formData.message.trim()) newErrors.message = 'Сообщение обязательно';
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    fetch('https://formspree.io/f/mvgbvnvr', {
      method: 'POST',
      body: JSON.stringify(formData),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (response.ok) {
          setIsSubmitted(true);
          setFormData({ name: '', email: '', message: '' });
          setTimeout(() => setIsSubmitted(false), 3000);
        } else {
          alert('Ошибка отправки. Попробуйте позже.');
        }
      })
      .catch((error) => {
        console.error('Ошибка:', error);
        alert('Произошла ошибка сети. Проверьте подключение.');
      });
  };

  const images = [
    'https://i.pinimg.com/736x/6b/c6/10/6bc610ccc015ef7fa26780003109ec88.jpg',
    'https://i.pinimg.com/1200x/6c/da/54/6cda54bd8a74a541f6643125bc66db6a.jpg',
    'https://i.pinimg.com/736x/36/82/35/36823531ede1c2ef0a52064c69a93332.jpg',
    'https://i.pinimg.com/736x/17/97/51/179751d9f8a0ced9198999ff110fc5c8.jpg',
    'https://i.pinimg.com/736x/7c/b2/c9/7cb2c90cee1b9dd23e17fa4c8ddea0dc.jpg',
    'https://i.pinimg.com/1200x/3d/a2/2a/3da22a246fcb45d7585b74401fa084c2.jpg',
  ];

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>Анна Иванова</h1>
          <p>Фотография, которая рассказывает историю</p>
        </div>
      </header>

      {/* About */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>О себе</h2>
          <p>
            Я — фотограф с более чем 5-летним опытом в портретной и уличной фотографии.
            Люблю запечатлевать моменты, которые кажутся незаметными, но полны эмоций.
            Работаю в мягких светах, с акцентом на естественность и детали.
          </p>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>Моя работа</h2>
          <div className="gallery">
            {images.map((img, index) => (
              <div key={index} className="gallery-item" style={{ animationDelay: `${index * 0.2}s` }}>
                <img src={img.trim()} alt={`Фото ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>Связаться со мной</h2>
          <form
            action="https://formspree.io/f/mvgbvnvr"
            method="POST"
            onSubmit={handleSubmit}
            className="contact-form"
          >
            {/* Скрытые поля для Formspree */}
            <input type="hidden" name="_subject" value="📩 Новое сообщение с портфолио фотографа" />
            <input type="hidden" name="_replyto" value={formData.email} />

            <div className="form-group">
              <label htmlFor="name">Имя</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Ваше имя"
              />
              {errors.name && <span style={{ color: '#e74c3c', fontSize: '0.8rem' }}>{errors.name}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Ваш email"
              />
              {errors.email && <span style={{ color: '#e74c3c', fontSize: '0.8rem' }}>{errors.email}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="message">Сообщение</label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                placeholder="Чем могу помочь?"
              />
              {errors.message && <span style={{ color: '#e74c3c', fontSize: '0.8rem' }}>{errors.message}</span>}
            </div>

            <button type="submit">Отправить</button>
          </form>

          {isSubmitted && (
            <p style={{ color: '#2ecc71', textAlign: 'center', marginTop: '1rem', fontWeight: 500 }}>
              ✅ Спасибо! Я получила ваше сообщение и скоро свяжусь с вами.
            </p>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer style={{ textAlign: 'center', padding: '2rem 0', backgroundColor: '#f2f8f2', borderTop: '1px solid #e0e0e0' }}>
        <div className="container">
          <p>&copy; 2025 Анна Иванова. Все права защищены.</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
