    { id: 10, category: 'природа', src: 'https://i.pinimg.com/736x/0d/ec/b0/0decb085cf9ad187d8f637bc42ab6aed.jpg', likes: 2200, alt: 'красивый пейзаж с цветами в поле' },
    { id: 11, category: 'природа', src: 'https://i.pinimg.com/1200x/19/2a/ec/192aeccaef61a979d339d9dc521fa219.jpg', likes: 1600, alt: 'цветы с книгой' },
    { id: 12, category: 'природа', src: 'https://i.pinimg.com/736x/29/cb/64/29cb64bc01849d9dccb8d7af177fd416.jpg', likes: 2000, alt: 'закат в поле' },

    // Люди
    { id: 13, category: 'люди', src: 'https://i.pinimg.com/736x/7d/d5/39/7dd539c448579c6093bb2368321628b3.jpg', likes: 1200, alt: 'Модель с черепашкой' },
    { id: 14, category: 'люди', src: 'https://i.pinimg.com/1200x/aa/95/dc/aa95dc33f16ee2d9cc2189e61434b5aa.jpg', likes: 1200, alt: 'Модель' },
    { id: 15, category: 'люди', src: 'https://i.pinimg.com/736x/c3/16/78/c316780894aa4d863808f6f755e65d5d.jpg', likes: 1700, alt: 'Кореянка' },
    { id: 16, category: 'люди', src: 'https://i.pinimg.com/1200x/67/90/96/6790963d1e4402f72b47789fe73c1477.jpg', likes: 1400, alt: 'Студия1' },
    { id: 17, category: 'люди', src: 'https://i.pinimg.com/736x/0b/3e/94/0b3e94f540c89db65a81fb49e63b7f62.jpg', likes: 1600, alt: 'Студия2' },
    { id: 18, category: 'люди', src: 'https://i.pinimg.com/1200x/66/fa/55/66fa55f2848485919c30c8d48ad06316.jpg', likes: 2600, alt: 'Студия3' },

    // Животные
    { id: 19, category: 'животные', src: 'https://i.pinimg.com/736x/da/a0/18/daa018275ddc5c84895ea8f9e48548be.jpg', likes: 2100, alt: 'Лошадки на лугу' },
    { id: 20, category: 'животные', src: 'https://i.pinimg.com/736x/1c/4e/29/1c4e29f9140f17129ed7d8c5d9a6ce85.jpg', likes: 1800, alt: 'Лиса с птичкой на носу' },
    { id: 21, category: 'животные', src: 'https://i.pinimg.com/736x/f5/b3/f4/f5b3f46fa487b61e48accdcca5d9b3e5.jpg', likes: 1900, alt: 'Мечтательный кот' },
    { id: 22, category: 'животные', src: 'https://i.pinimg.com/736x/ec/c7/4f/ecc74f5e0fcdc2c97be045111cd98a4b.jpg', likes: 2000, alt: 'Кот и закат' },
    { id: 23, category: 'животные', src: 'https://i.pinimg.com/736x/fc/86/1c/fc861cb83a92c8dd62f61446fbfdc0cc.jpg', likes: 1700, alt: 'Далматинец' },
    { id: 24, category: 'животные', src: 'https://i.pinimg.com/736x/c1/8f/e9/c18fe9d8618b7be34ff7aa36e57abc3c.jpg', likes: 1700, alt: 'Шпиц' },
    { id: 25, category: 'животные', src: 'https://i.pinimg.com/736x/10/af/11/10af11672fd2c25a8bc2b5d545b5e26b.jpg', likes: 1700, alt: 'Миниатюрный пудель' },
    { id: 26, category: 'животные', src: 'https://i.pinimg.com/736x/ee/d9/28/eed928c1c071107ff2c39d16b085a640.jpg', likes: 1700, alt: 'Панда в зоопарке' },
    { id: 27, category: 'животные', src: '/images/cat.jpg', likes: 3700, alt: 'Саня' }
  ];

  useEffect(() => {
    const initialLikes = {};
    photos.forEach(photo => {
      initialLikes[photo.id] = photo.likes;
    });
    setLikes(initialLikes);

    const initialComments = {};
    photos.forEach(photo => {
      let categoryComments = [];

      // Определяем тематические комментарии для животных
      if (photo.category === 'животные') {
        const alt = photo.alt.toLowerCase();
        if (alt.includes('кошк') || alt.includes('кот') || alt.includes('саня')) {
          categoryComments = commentsByCategory.животные.кошки;
        } else if (alt.includes('собак') || alt.includes('далматинец') || alt.includes('питомец') || alt.includes('шпиц') || alt.includes('пудель')) {
          categoryComments = commentsByCategory.животные.собаки;
        } else if (alt.includes('лис')) {
          categoryComments = commentsByCategory.животные.лисы;
        } else if (alt.includes('панда')) {
          categoryComments = commentsByCategory.животные.панды;
        } else if (alt.includes('лошад') || alt.includes('конь')) {
          categoryComments = commentsByCategory.животные.лошади;
        }
      } else {
        categoryComments = commentsByCategory[photo.category] || [];
      }

      const count = Math.floor(Math.random() * 28) + 18;
      initialComments[photo.id] = Array.from({ length: count }, (_, i) => {
        const commentText = categoryComments[Math.floor(Math.random() * categoryComments.length)];
        const author = names[Math.floor(Math.random() * names.length)];
        return {
          id: i + 1,
          text: commentText,
          author,
          date: new Date(Date.now() - Math.random() * 1000000000).toLocaleDateString()
        };
      });
    });
    setComments(initialComments);
  }, []);

  const handleLike = (id) => {
    if (likedPhotos.has(id)) {
      setLikedPhotos(prev => {
        const newSet = new Set(prev);
        newSet.delete(id);
        return newSet;
      });
      setLikes(prev => ({
        ...prev,
        [id]: prev[id] - 1
      }));
    } else {
      setLikedPhotos(prev => new Set(prev).add(id));
      setLikes(prev => ({
        ...prev,
        [id]: prev[id] + 1
      }));
    }
  };

  const openModal = (photo) => {
    setSelectedPhoto(photo);
    setIsModalOpen(true);
  };

  const addComment = () => {
    if (!newComment.trim()) return;
    const comment = {
      id: Date.now(),
      text: newComment,
      author: 'Анна Иванова',
      date: new Date().toLocaleDateString()
    };
    setComments(prev => ({
      ...prev,
      [selectedPhoto.id]: [...prev[selectedPhoto.id], comment]
    }));
    setNewComment('');
  };

  const filteredPhotos = activeCategory === 'все'
    ? photos
    : photos.filter(photo => photo.category === activeCategory);

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>Анна Иванова</h1>
          <p>Фотография, которая рассказывает историю</p>
        </div>
      </header>

      {/* Categories */}
      <section className="section fade-in-up">
        <div className="container">
          <div className="categories-container">
            {['все', 'архитектура', 'природа', 'люди', 'животные'].map((cat) => (
              <button
                key={cat}
                onClick={() => setActiveCategory(cat)}
                className={`category-btn ${activeCategory === cat ? 'active' : ''}`}
              >
                {cat === 'все' ? 'Все' : cat.charAt(0).toUpperCase() + cat.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2 className="section-title">
            {activeCategory === 'все' ? 'Моя работа' : 
             activeCategory === 'архитектура' ? 'Архитектура' : 
             activeCategory === 'природа' ? 'Природа' : 
             activeCategory === 'люди' ? 'Люди' : 'Животные'}
          </h2>
          <div className="gallery">
            {filteredPhotos.map((photo, index) => (
              <div key={photo.id} className="gallery-item" style={{ animationDelay: `${index * 0.1}s` }}>
                <img src={photo.src.trim()} alt={photo.alt} />
                <div className="gallery-overlay">
                  <div className="like-container" onClick={() => handleLike(photo.id)}>
                    <span className="like-icon">
                      {likedPhotos.has(photo.id) ? '❤️' : '🤍'}
                    </span>
                    <span className="like-count">{likes[photo.id]}</span>
                  </div>
                  <button
                    onClick={() => openModal(photo)}
                    className="view-btn"
                  >
                    <span className="btn-text">Перейти</span>
                    <span className="btn-icon">→</span>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2 className="section-title">Связаться со мной</h2>
          <form
            action="https://formspree.io/f/mvgbvnvr"
            method="POST"
            className="contact-form"
          >
            <input type="hidden" name="_subject" value="📩 Новое сообщение с портфолио фотографа" />
            <div className="form-group">
              <label htmlFor="name">Имя</label>
              <input type="text" id="name" name="name" placeholder="Ваше имя" required />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="email" id="email" name="email" placeholder="Ваш email" required />
            </div>
            <div className="form-group">
              <label htmlFor="message">Сообщение</label>
              <textarea id="message" name="message" placeholder="Чем могу помочь?" required></textarea>
            </div>
            <button type="submit" className="submit-btn">Отправить</button>
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <p>&copy; 2025 Анна Иванова. Все права защищены.</p>
        </div>
      </footer>

      {/* Modal */}
      {isModalOpen && selectedPhoto && (
        <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3 className="modal-title">Комментарии: {selectedPhoto.alt}</h3>
            <img src={selectedPhoto.src.trim()} alt={selectedPhoto.alt} className="modal-image" />
            <h4>Комментарии ({comments[selectedPhoto.id]?.length || 0})</h4>
            <div className="comments-container">
              {comments[selectedPhoto.id]?.map(comment => (
                <div key={comment.id} className="comment-item">
                  <strong>{comment.author}</strong> • {comment.date}
                  <p>{comment.text}</p>
                </div>
              ))}
            </div>
            <div className="comment-form">
              <label>Написать комментарий:</label>
              <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Введите ваш комментарий..."
                rows="3"
              />
              <button onClick={addComment} className="comment-submit-btn">
                Отправить
              </button>
            </div>
            <button
              onClick={() => setIsModalOpen(false)}
              className="modal-close-btn"
            >
              Закрыть
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
