// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeCategory, setActiveCategory] = useState('–≤—Å–µ');
  const [likes, setLikes] = useState({});
  const [likedPhotos, setLikedPhotos] = useState(new Set());
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  // –ò–º–µ–Ω–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π (—Ä–µ–∞–ª–∏—Å—Ç–∏—á–Ω—ã–µ)
  const userNames = [
    '–ú–∞—Ä–∏—è', '–î–º–∏—Ç—Ä–∏–π', '–ê–Ω–Ω–∞', '–ò–≤–∞–Ω', '–ï–ª–µ–Ω–∞', '–ê–ª–µ–∫—Å–µ–π', '–û–ª—å–≥–∞', '–°–µ—Ä–≥–µ–π', '–ù–∞—Ç–∞–ª—å—è', '–ö—Å–µ–Ω–∏—è',
    '–ú–∏—Ö–∞–∏–ª', '–¢–∞—Ç—å—è–Ω–∞', '–ê—Ä—Ç—ë–º', '–í–∏–∫—Ç–æ—Ä–∏—è', '–ü–∞–≤–µ–ª', '–Æ–ª–∏—è', '–†–æ–º–∞–Ω', '–õ—é–¥–º–∏–ª–∞', '–°—Ç–∞–Ω–∏—Å–ª–∞–≤', '–î–∞—Ä—å—è'
  ];

  // –¢–µ–º–∞—Ç–∏—á–µ—Å–∫–∏–µ –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
  const commentsByCategory = {
    –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞: [
      '–ö–∞–∫–æ–µ –≤–ø–µ—á–∞—Ç–ª—è—é—â–µ–µ –∑–¥–∞–Ω–∏–µ!',
      '–§–æ—Ç–æ –ø–µ—Ä–µ–¥–∞—ë—Ç –∞—Ç–º–æ—Å—Ñ–µ—Ä—É –≤—Ä–µ–º–µ–Ω–∏',
      '–õ—é–±–ª—é —Ç–∞–∫–∏–µ –¥–µ—Ç–∞–ª–∏ –≤ –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–µ',
      '–°–æ–≤—Ä–µ–º–µ–Ω–Ω–æ –∏ –∫—Ä–∞—Å–∏–≤–æ',
      '–≠—Ç–æ —à–µ–¥–µ–≤—Ä –∏–Ω–∂–µ–Ω–µ—Ä–Ω–æ–π –º—ã—Å–ª–∏',
      '–û—á–µ–Ω—å –≥–∞—Ä–º–æ–Ω–∏—á–Ω—ã–µ –ø—Ä–æ–ø–æ—Ä—Ü–∏–∏',
      '–°–≤–µ—Ç –∏ —Ç–µ–Ω—å –∏–¥–µ–∞–ª—å–Ω–æ –ø–æ–¥–æ–±—Ä–∞–Ω—ã',
      '–•–æ—á—É –ø–æ–±—ã–≤–∞—Ç—å –≤ —Ç–∞–∫–æ–º –º–µ—Å—Ç–µ!',
      '–ê—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞ –±—É–¥—É—â–µ–≥–æ',
      '–ö–∞–∂–¥—ã–π —ç–ª–µ–º–µ–Ω—Ç –Ω–∞ —Å–≤–æ—ë–º –º–µ—Å—Ç–µ'
    ],
    –ø—Ä–∏—Ä–æ–¥–∞: [
      '–ü—Ä–∏—Ä–æ–¥–∞ ‚Äî –ª—É—á—à–∏–π —Ö—É–¥–æ–∂–Ω–∏–∫',
      '–¢–∞–∫–æ–µ —Å–ø–æ–∫–æ–π—Å—Ç–≤–∏–µ –∏ —É–º–∏—Ä–æ—Ç–≤–æ—Ä–µ–Ω–∏–µ',
      '–í–æ—Ç —ç—Ç–æ –∑–∞–∫–∞—Ç‚Ä¶ –ë–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ!',
      '–õ—é–±–ª—é —Ç–∞–∫–∏–µ –ø–µ–π–∑–∞–∂–∏',
      '–ú–µ—á—Ç–∞—é –ø–æ–±—ã–≤–∞—Ç—å –≤ —Ç–∞–∫–∏—Ö –º–µ—Å—Ç–∞—Ö',
      '–§–æ—Ç–æ –¥—ã—à–∏—Ç –∂–∏–∑–Ω—å—é',
      '–û—â—É—â–∞—é –∑–∞–ø–∞—Ö –ª–µ—Å–∞ –∏ –≤–µ—Ç–µ—Ä',
      '–ü—Ä–∏—Ä–æ–¥–∞ –≤–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ—Ç –¥—É—à—É',
      '–ö—Ä–∞—Å–æ—Ç–∞, –æ—Ç –∫–æ—Ç–æ—Ä–æ–π –∑–∞—Ö–≤–∞—Ç—ã–≤–∞–µ—Ç –¥—É—Ö',
      '–ó–¥–µ—Å—å —á—É–≤—Å—Ç–≤—É–µ—à—å —Å–≤—è–∑—å —Å –∑–µ–º–ª—ë–π'
    ],
    –ª—é–¥–∏: [
      '–≠–º–æ—Ü–∏–∏ –ø–µ—Ä–µ–¥–∞–Ω—ã –∏–¥–µ–∞–ª—å–Ω–æ',
      '–ì–ª–∞–∑–∞ —Ä–∞—Å—Å–∫–∞–∂—É—Ç –±–æ–ª—å—à–µ —Å–ª–æ–≤',
      '–ß—É–≤—Å—Ç–≤—É–µ—Ç—Å—è –∏—Å—Ç–æ—Ä–∏—è –∑–∞ –∫–∞–¥—Ä–æ–º',
      '–¢–∞–∫–∞—è –∏—Å–∫—Ä–µ–Ω–Ω–æ—Å—Ç—å ‚Äî —Ä–µ–¥–∫–æ—Å—Ç—å',
      '–§–æ—Ç–æ —Å–ª–æ–≤–Ω–æ —Ñ–∏–ª—å–º',
      '–õ—é–±–ª—é —Ç–∞–∫–∏–µ –º–æ–º–µ–Ω—Ç—ã',
