  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è URL
  const updateUrl = (category) => {
    const newUrl = category === '–≤—Å–µ' 
      ? window.location.pathname 
      : `${window.location.pathname}#${category}`;
    window.history.replaceState(null, null, newUrl);
