// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeCategory, setActiveCategory] = useState('все');
  const [likes, setLikes] = useState({});
  const [likedPhotos, setLikedPhotos] = useState(new Set());
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [comments, setComments] = useState({}); // ID фото → массив комментариев
  const [newComment, setNewComment] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Список комментариев (примерные)
  const sampleComments = [
    'Красиво!',
    'Очень атмосферное фото',
    'Мне нравится свет',
    'Супер!',
    'Это же мечта!',
    'Вдохновляет',
    'Такая красота',
    'Я бы здесь жил',
    'Фотография как картина',
    'Очень душевное',
    'Замечательная композиция',
    'Спасибо за эмоции',
    'Какое чудо!',
    'Архитектура на высшем уровне',
    'Расслабляющее фото',
    'Я в восторге',
    'Надо сюда приехать',
    'Идеальный кадр',
    'Ты талантливая!',
    'Это просто фантастика',
    'Чувства переданы идеально',
    'Гениально',
    'Одно из лучших фото',
    'Без слов',
    'Магия',
    'Видно, что это искусство',
    'Удивительный взгляд',
    'Жизнь в деталях',
    'Ты вдохновляешь',
    'Это счастье!',
    'Прекрасно',
    'Такие моменты нужно запечатлевать',
    'Ты видишь то, что другие не замечают',
    'Мне очень понравилось',
    'Душевное фото',
    'Спасибо за красоту'
  ];

  const photos = [
  // Архитектура — твои фото
  {
    id: 1,
    category: 'архитектура',
    src: 'https://i.pinimg.com/736x/6b/c6/10/6bc610ccc015ef7fa26780003109ec88.jpg',
    likes: 1200,
    alt: 'Здание в стиле модерн'
  },
  {
    id: 2,
    category: 'архитектура',
    src: 'https://i.pinimg.com/1200x/6c/da/54/6cda54bd8a74a541f6643125bc66db6a.jpg',
    likes: 2100,
    alt: 'Лесной пейзаж' // временно оставим, потом исправим
  },
  {
    id: 3,
    category: 'архитектура',
    src: 'https://i.pinimg.com/736x/36/82/35/36823531ede1c2ef0a52064c69a93332.jpg',
    likes: 1500,
    alt: 'Портрет девушки на улице'
  },
  {
    id: 4,
    category: 'архитектура',
    src: 'https://i.pinimg.com/736x/17/97/51/179751d9f8a0ced9198999ff110fc5c8.jpg',
    likes: 2800,
    alt: 'Кот на крыше'
  },
  {
    id: 5,
    category: 'архитектура',
    src: 'https://i.pinimg.com/736x/7c/b2/c9/7cb2c90cee1b9dd23e17fa4c8ddea0dc.jpg',
    likes: 1800,
    alt: 'Старинная башня'
  },
  {
    id: 6,
    category: 'архитектура',
    src: 'https://i.pinimg.com/1200x/3d/a2/2a/3da22a246fcb45d7585b74401fa084c2.jpg',
    likes: 3000,
    alt: 'Река в горах'
  },

  // Природа — 7 рандомных фото
  {
    id: 7,
    category: 'природа',
    src: 'https://i.pinimg.com/1200x/7f/e4/20/7fe42026a9f21b53bd5e6df4303423bb.jpg',
    likes: 1300,
    alt: 'Лес в тумане'
  },
  {
    id: 8,
    category: 'природа',
    src: 'https://i.pinimg.com/736x/dc/1a/91/dc1a917c3a4860887f9c89c0345e0292.jpg',
    likes: 1700,
    alt: 'Озеро в горах'
  },
  {
    id: 9,
    category: 'природа',
    src: 'https://i.pinimg.com/736x/d9/e0/09/d9e00996575cc3ec05bbc36be4ff2042.jpg',
    likes: 1400,
    alt: 'Солнце над морем'
  },
  {
    id: 10,
    category: 'природа',
    src: 'https://i.pinimg.com/736x/cb/89/fc/cb89fc3ac1c3a1d61e4762bd9e03fe6a.jpg',
    likes: 1900,
    alt: 'Дождь в лесу'
  },
  {
    id: 11,
    category: 'природа',
    src: 'https://i.pinimg.com/736x/0d/ec/b0/0decb085cf9ad187d8f637bc42ab6aed.jpg',
    likes: 2200,
    alt: 'Травы на ветру'
  },
  {
    id: 12,
    category: 'природа',
    src: 'https://i.pinimg.com/1200x/19/2a/ec/192aeccaef61a979d339d9dc521fa219.jpg',
    likes: 1600,
    alt: 'Закат над полем'
  },
  {
    id: 13,
    category: 'природа',
    src: 'https://i.pinimg.com/736x/29/cb/64/29cb64bc01849d9dccb8d7af177fd416.jpg',
    likes: 2000,
    alt: 'Гора в облаках'
  },

  // Люди — 6 рандомных фото
  {
    id: 14,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1573497645366-417333d5790d?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1200,
    alt: 'Женщина в городе'
  },
  {
    id: 15,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1570751520803-2341f03c6e4c?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1500,
    alt: 'Мужчина на улице'
  },
  {
    id: 16,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1573497645366-417333d5790d?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1300,
    alt: 'Девушка с сумкой'
  },
  {
    id: 17,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1557804506-669a67965ba0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1700,
    alt: 'Улыбка на улице'
  },
  {
    id: 18,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1541807819-031222791083?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1400,
    alt: 'Подросток с телефоном'
  },
  {
    id: 19,
    category: 'люди',
    src: 'https://images.unsplash.com/photo-1565299624946-b28f69b8d818?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1600,
    alt: 'Группа друзей'
  },

  // Животные — 5 рандомных фото
  {
    id: 20,
    category: 'животные',
    src: 'https://i.pinimg.com/736x/da/a0/18/daa018275ddc5c84895ea8f9e48548be.jpg',
    likes: 2100,
    alt: 'Кот на подоконнике'
  },
  {
    id: 21,
    category: 'животные',
    src: 'https://i.pinimg.com/1200x/a2/01/b3/a201b3a5026b72d5611cc3817546fb0b.jpg',
    likes: 1800,
    alt: 'Собака на прогулке'
  },
  {
    id: 22,
    category: 'животные',
    src: 'https://images.unsplash.com/photo-1584272254548-889491905322?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1900,
    alt: 'Птица на ветке'
  },
  {
    id: 23,
    category: 'животные',
    src: 'https://images.unsplash.com/photo-1589715814818-78e10434146e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 2000,
    alt: 'Кот в окне'
  },
  {
    id: 24,
    category: 'животные',
    src: 'https://images.unsplash.com/photo-1570553195323-971505932324?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    likes: 1700,
    alt: 'Котенок спит'
  }
];

  useEffect(() => {
    const initialLikes = {};
    photos.forEach(photo => {
      initialLikes[photo.id] = photo.likes;
    });
    setLikes(initialLikes);

    // Инициализируем комментарии для каждого фото (случайно от 18 до 45)
    const initialComments = {};
    photos.forEach(photo => {
      const count = Math.floor(Math.random() * 28) + 18; // 18–45
      initialComments[photo.id] = Array.from({ length: count }, (_, i) => {
        const comment = sampleComments[Math.floor(Math.random() * sampleComments.length)];
        return {
          id: i + 1,
          text: comment,
          author: 'Аноним', // Можно сделать случайные имена
          date: new Date(Date.now() - Math.random() * 1000000000).toLocaleDateString()
        };
      });
    });
    setComments(initialComments);
  }, []);

  const handleLike = (id) => {
    if (likedPhotos.has(id)) {
      setLikedPhotos(prev => {
        const newSet = new Set(prev);
        newSet.delete(id);
        return newSet;
      });
      setLikes(prev => ({
        ...prev,
        [id]: prev[id] - 1
      }));
    } else {
      setLikedPhotos(prev => new Set(prev).add(id));
      setLikes(prev => ({
        ...prev,
        [id]: prev[id] + 1
      }));
    }
  };

  const openModal = (photo) => {
    setSelectedPhoto(photo);
    setIsModalOpen(true);
  };

  const addComment = () => {
    if (!newComment.trim()) return;

    const comment = {
      id: Date.now(),
      text: newComment,
      author: 'Анна Иванова', // Ты
      date: new Date().toLocaleDateString()
    };

    setComments(prev => ({
      ...prev,
      [selectedPhoto.id]: [...prev[selectedPhoto.id], comment]
    }));

    setNewComment('');
  };

  const filteredPhotos = activeCategory === 'все'
    ? photos
    : photos.filter(photo => photo.category === activeCategory);

  return (
    <div className="App">
      {/* Header */}
      <header className="header fade-in-up">
        <div className="container">
          <h1>Анна Иванова</h1>
          <p>Фотография, которая рассказывает историю</p>
        </div>
      </header>

      {/* Categories */}
      <section className="section fade-in-up">
        <div className="container">
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '2rem' }}>
            {['все', 'архитектура', 'природа', 'люди', 'животные'].map((cat) => (
              <button
                key={cat}
                onClick={() => setActiveCategory(cat)}
                className={`category-btn ${activeCategory === cat ? 'active' : ''}`}
              >
                {cat === 'все' ? 'Все' : cat.charAt(0).toUpperCase() + cat.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Gallery */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>{activeCategory === 'все' ? 'Моя работа' : activeCategory === 'архитектура' ? 'Архитектура' : 
               activeCategory === 'природа' ? 'Природа' : 
               activeCategory === 'люди' ? 'Люди' : 'Животные'}</h2>
          <div className="gallery">
            {filteredPhotos.map((photo) => (
              <div key={photo.id} className="gallery-item" style={{ animationDelay: `${photo.id * 0.2}s` }}>
                <img src={photo.src} alt={photo.alt} />
                <div className="like-container" onClick={() => handleLike(photo.id)}>
                  <span className="like-icon">
                    {likedPhotos.has(photo.id) ? '❤️' : '🤍'}
                  </span>
                  <span className="like-count">{likes[photo.id]}</span>
                </div>
                <button
                  onClick={() => openModal(photo)}
                  className="comment-btn"
                  style={{
                    position: 'absolute',
                    bottom: '1rem',
                    left: '1rem',
                    padding: '0.3rem 0.8rem',
                    background: 'rgba(255,255,255,0.8)',
                    border: 'none',
                    borderRadius: '20px',
                    fontSize: '0.8rem',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                >
                  Перейти
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact */}
      <section className="section fade-in-up">
        <div className="container">
          <h2>Связаться со мной</h2>
          <form
            action="https://formspree.io/f/mvgbvnvr"
            method="POST"
            className="contact-form"
          >
            <input type="hidden" name="_subject" value="📩 Новое сообщение с портфолио фотографа" />

            <div className="form-group">
              <label htmlFor="name">Имя</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Ваше имя"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Ваш email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="message">Сообщение</label>
              <textarea
                id="message"
                name="message"
                placeholder="Чем могу помочь?"
                required
              ></textarea>
            </div>

            <button type="submit">Отправить</button>
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer style={{ textAlign: 'center', padding: '2rem 0', backgroundColor: '#f2f8f2', borderTop: '1px solid #e0e0e0' }}>
        <div className="container">
          <p>&copy; 2025 Анна Иванова. Все права защищены.</p>
        </div>
      </footer>

      {/* Modal */}
      {isModalOpen && selectedPhoto && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          backdropFilter: 'blur(4px)'
        }}>
          <div style={{
            background: 'white',
            width: '90%',
            maxWidth: '600px',
            borderRadius: '16px',
            padding: '2rem',
            boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
            maxHeight: '90vh',
            overflowY: 'auto'
          }}>
            <h3 style={{ textAlign: 'center', margin: '0 0 1rem 0' }}>
              Комментарии к фото: {selectedPhoto.alt}
            </h3>
            <div style={{ marginBottom: '1.5rem' }}>
              <img src={selectedPhoto.src} alt={selectedPhoto.alt} style={{ width: '100%', height: 'auto', borderRadius: '12px' }} />
            </div>
            <div style={{ marginBottom: '1.5rem' }}>
              <h4>Комментарии ({comments[selectedPhoto.id]?.length || 0})</h4>
              <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
                {comments[selectedPhoto.id]?.map(comment => (
                  <div key={comment.id} style={{
                    padding: '0.8rem',
                    borderBottom: '1px solid #eee',
                    fontSize: '0.95rem',
                    color: '#555'
                  }}>
                    <strong>{comment.author}</strong> • {comment.date}
                    <p style={{ margin: '0.3rem 0' }}>{comment.text}</p>
                  </div>
                ))}
              </div>
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              <label htmlFor="comment-input">Написать комментарий:</label>
              <textarea
                id="comment-input"
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Введите ваш комментарий..."
                style={{ padding: '0.8rem', border: '1px solid #ddd', borderRadius: '8px' }}
                rows="3"
              ></textarea>
              <button
                onClick={addComment}
                style={{
                  padding: '0.7rem',
                  background: '#a8e6cf',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '500'
                }}
              >
                Отправить
              </button>
            </div>
            <button
              onClick={() => setIsModalOpen(false)}
              style={{
                marginTop: '1.5rem',
                padding: '0.7rem',
                background: '#e0e0e0',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                alignSelf: 'center'
              }}
            >
              Закрыть
